ody{\npadding: 20px;\n}\n\n#content, .container-fluid, .span9\n{\n    overflow-y:auto;\n    height:100%;\n}\n\n\n/*remove review section from single course \n.course_reviews{display:none;}*/\n.course_activities{display:none;}\ndiv.review_breakup{display:none;}\ndiv.rating_snapshot{display:none;}\n\n/*remove students taking course count from single course*/\n#buddypress div#item-header div#item-meta .students{\ndisplay:none;\n}\n\n\n.login label {\n    color: #505050;\n}\n\n\n\n\n/*header position fix on scroll, the pageid+tag does not work, but the rest does. if want fixed on scroll , change position to fixed; include transparent header effect*/\n/*\n.page-id-4361 header{\n  position: absolute;\n  top: 0;\n  left: 0;\n  z-index: 999;\n  width: 100%;\n  height: 20px;\n  background: rgba(255,255,255,0.7);\n}\n*/\nnav .menu li a {\ntext-transform: capitalize;\nfont-size: 16px;\nfont-family: inherit;\n}\n\nnav .sub-menu {\n border: 2px solid #f0f0f0;\n background-color: #ffffff;\n}\n\nnav .menu li:hover>.sub-menu>li{\n color: #f7632c;\n font-family: Lato, \"Open Sans\";\n font-size: 0.95em;\n}\n\nnav .sub-menu li:hover{\n background-color: #f0f0f0;\n}\n\n/*\n.page-id-4361 nav .menu li a {\ncolor:white;\n}*/\n\n#register-page h2{\n color:#f7931e;\nfont-size: 1.5em;\nfont-family:  Lato, \"Open Sans\";\nfont-weight: 500;\n}\n\n#register-page h5{\nfont-size: 14px;\nmargin-bottom:0px;\n}\n\n.register-section{\nmargin-bottom: 10px;\nmargin-top: 10px;\n}\n\n.page-id-0 #content{\nbackground-color: #f0f0f0;\n}\n#register-page div.content.padder {\nbackground:white;\n}\n\n/*remove instructor from single course page*/\n.single-course #item-admins{display:none;}\n.instructor_meta {\n}\n\n/*remove student name list from the single course page*/\n.students_undertaking{display:none;}\n\n/*hide breakcrumbs although it isstill loaded */\n.breadcrumbs{display:none;}\n\n/*hide toggle field visibility in registeration page*/\n.field-visibility-settings-notoggle, .field-visibility-settings-